import { useCallback, useState } from 'react';
import useEventListener from './useEventListener';
/**
 * Returns current scroll left position in pixels.
 *
 * @returns {number} Scroll left position in pixels
 */

export default function useScrollLeft() {
  var _useState = useState(window.pageXOffset),
      scrollLeft = _useState[0],
      setScrollLeft = _useState[1];

  var getScrollLeft = useCallback(function () {
    return setScrollLeft(window.pageXOffset);
  }, []);
  useEventListener(document, 'scroll', getScrollLeft);
  return scrollLeft;
}
