import { useCallback, useState } from 'react';
import useEventListener from './useEventListener';
/**
 * Returns the interior height of the window in pixels.
 *
 * @returns {number} Height of the window in pixels
 */

export default function useWindowHeight() {
  var _useState = useState(window.innerHeight),
      windowHeight = _useState[0],
      setWindowHeight = _useState[1];

  var getWindowHeight = useCallback(function () {
    return setWindowHeight(window.innerHeight);
  }, []);
  useEventListener(window, 'resize', getWindowHeight);
  return windowHeight;
}
