import { useCallback, useState } from 'react';
import useEventListener from './useEventListener';
/**
 * Returns the online status of the browser.
 *
 * @returns {Boolean} Online status of the browser
 */

export default function useOnLine() {
  var _useState = useState(window.navigator.onLine),
      onLine = _useState[0],
      setOnLine = _useState[1];

  var handleOnline = useCallback(function () {
    setOnLine(true);
  }, []);
  var handleOffline = useCallback(function () {
    setOnLine(false);
  }, []);
  useEventListener(window, 'online', handleOnline);
  useEventListener(window, 'offline', handleOffline);
  return onLine;
}
