import { useCallback, useState } from 'react';
import useEventListener from './useEventListener';
/**
 * Returns current scroll top position in pixels.
 *
 * @returns {number} Scroll top position in pixels
 */

export default function useScrollTop() {
  var _useState = useState(window.pageYOffset),
      scrollTop = _useState[0],
      setScrollTop = _useState[1];

  var getScrollTop = useCallback(function () {
    return setScrollTop(window.pageYOffset);
  }, []);
  useEventListener(document, 'scroll', getScrollTop);
  return scrollTop;
}
